mapper.select:
select

from blog
where is_deleted=0

  and (blog_title like concat("%", #{keyword}, "%") or blog_category_name like concat("%", #{keyword}, "%")
    or blog_content like concat("%", #{keyword}, "%")
  )


  and blog_status = #{blogStatus}


  and blog_category_id = #{blogCategoryId}

order by create_time desc

select b.blog_id, b.blog_title, b.blog_sub_url, b.blog_cover_image, b.blog_content, b.blog_category_id, b.blog_category_name, b.blog_tags, b.blog_status, b.blog_views, b.enable_comment, b.is_deleted, b.create_time, b.update_time from blog b inner join blog_tag_relation bt on b.blog_id = bt.blog_id inner join blog_tag t on bt.tag_id = t.tag_id where b.is_deleted = 0
and t.tag_name = #{tagName}
order by b.create_time desc
